import React, {useContext, useState} from 'react'
import {TableContext} from '../model/context'
import Model from "../model/model";
const Settings= ()=> { 
    const [columns, setColumns] = useContext(TableContext);
    const model = Model();
    const handleSelect = (e)=>{
        setColumns(...columns, e.target.name)

    }
    const addColumn = (e)=>{
        e.preventDefault();
    }
    return (
        <div>
            <form onSubmit = {addColumn}>
                {columns.map(column => { return !column.checked
                 ?
                <>    
                    <label for={column.name}>{column.name}</label>    
                    <input onClick={handleSelect} type="checkbox" value={column.name} name={column.name} />
                </>
                 :
                 <>
                 <label for={column.name}>{column.name}</label>    
                    <input onChange={handleSelect} type="checkbox" value={column.name} name={column.name} checked />
                </>
                  })}
                    <input type="submit" value="Save"/>     
            </form>
        </div>
    )
                }
export default Settings;
