import React, {useContext, useState} from 'react'
import {TableContext} from '../model/context'
import Model from "../model/model";
const Settings= ()=> { 
    const [columns, setColumns] = useContext(TableContext);
    const model = Model();
    let selections = columns;
    const handleSelect = (e)=>{
        selections.forEach(selection =>{
            if(selection.id === e.target.id){
                selection.checked = e.target.checked
            }
        })

    }
    const addColumn = (e)=>{
        e.preventDefault();
        setColumns(selections);
    }
    return (
        <div>
            <form onSubmit = {addColumn}>
                {columns.map(column => { return !column.checked
                 ?
                <>    
                    <label for={column.name}>{column.name}</label>    
                    <input onClick={handleSelect} type="checkbox" value={column.name} name={column.name} id={column.id}/>
                </>
                 :
                 <>
                 <label for={column.name}>{column.name}</label>    
                    <input onChange={handleSelect} type="checkbox" value={column.name} name={column.name} id={column.id} checked />
                </>
                  })}
                    <input type="submit" value="Save"/>     
            </form>
        </div>
    )
                }
export default Settings;
